import { useState, clearState, useRef } from "react";
import Modal, { setAppElement } from 'react-modal'
import { Container } from 'reactstrap';

const JumpGame = () => {
    var audio = new Audio('http://www.superluigibros.com/downloads/sounds/SNES/SMK/wav/jump.wav');
    var audio2 = new Audio('http://web.mit.edu/GRAPHICS/src/tuxpaint-0.9.12/data/sounds/bleep.wav');
    var audio3 = new Audio('http://www.mario-museum.net/sons/smb_gameover.wav');
    const character = useRef();
    const block = useRef();
    const characterTop = useRef()
    const blockLeft = useRef()

    let timeLeft = 3;
    var userScore = 0;
    const [countDown, setCountdown] = useState(timeLeft);
    const [score, setScore] = useState(userScore);
    const [modalIsOpen, setModalIsOpen] = useState(false);



    block.current = document.getElementById("block")
    character.current = document.getElementById("character")
    
    characterTop.current = parseInt(window.getComputedStyle(document.getElementById("character")).getPropertyValue("top"));
    blockLeft.current = parseInt(window.getComputedStyle(document.getElementById("block")).getPropertyValue("left"));

    const startGame = () => {
        audio2.play();
        setInterval(function () {
            if (timeLeft <= 0) {
                clearInterval(timeLeft = 0)
            } else {
                timeLeft -= 1
                setCountdown(timeLeft)
                audio2.play();
            }
        }, 1000)

        setTimeout(function () {

            block.current.style.animation = " block 1s infinite";
            const checkDead = setInterval(function () {
               
                if ((blockLeft.current) < 20 && (blockLeft.current) > 0 && characterTop.current >= 130) {
                    setModalIsOpen(true);
                    block.current.style.animation = "none";
                    audio3.play();
                    clearInterval(checkDead)
                }else if(character.current === null || block.current === null){
                    return
                }
                else{
                    userScore++
                    setScore(userScore)
                }

            }, 10)
        }, 4000)
    }

    const jump = () => {
        character = document.getElementById("character");
        if (character.classList != "animate") {
            character.classList.add("animate")
        }
        setTimeout(function () {
            // audio.play()
            character.classList.remove("animate")
        }, 500)

    }

    return (

        <div className="jumpgame">
            <div className="top">
                <h1>JUMP GAME</h1>
                <button onClick={() => { startGame(); }}>
                    start
                        </button>
                <h3><span>{countDown}</span></h3>
                <h5>Score: {score}</h5>
            </div>
            <div id="game" onClick={() => { jump(); }}>
                <div ref={character} id="character"></div>
                <div ref={block}id="block"></div>
            </div>
            <Container id="mod">
                <Modal className="Modal" ariaHideApp={false} isOpen={modalIsOpen} onRequestClose={() => { setModalIsOpen(false); timeLeft = 3; setCountdown(timeLeft); setScore(0) }}>
                    <h1>Game Over!</h1>
                    <h2>Your Score: {score}</h2>
                </Modal>
            </Container>
        </div>
    );
}

export default JumpGame;
